import { useDispatch, useSelector } from 'react-redux';
import './App.scss';
import { MusicTrack } from './assets/musics';

function App() {
  const dispatch = useDispatch()
  const { tracks, status } = useSelector((state: any) => state.tracks);

  const addTrack = (track: MusicTrack) => {
    dispatch({
      type: "ADD_TRACK",
      payload: track
    });

    const removeTrack = (track: MusicTrack) => {
      dispatch({
        type: "REMOVE_TRACK",
        payload: track
      });
    };

    const updateTrack = (track: MusicTrack, updatedData: Partial<MusicTrack>) => {
      dispatch({
        type: "UPDATE_TRACK",
        payload: {
          id: track.id,
          updatedData
        }
      });
    };
  };
  return (

    <div className="App">
      <p>Application </p>
    </div>

  );
}

export default App;
