import { Provider, useDispatch, useSelector } from 'react-redux';
import './App.scss';
import { MusicTrack } from './assets/musics';
import storeRedux, { AppDispatch } from './redux/store';
import { useEffect, useState } from 'react';
import { loadAsync } from './redux/rootReducer';

enum Direction {
  UP = -1,
  DOWN = 1
}

function App() {
  const dispatch = useDispatch<AppDispatch>()
  const { tracks, status } = useSelector((state: any) => state.musics);
  const { page, itemPerPage, filter } = useSelector((state: any) => state.filter)
  const [filteredTracks, setFilteredTracks] = useState(tracks)

  const [allLiked, setAllLiked] = useState<string[]>([])
  const [allDisliked, setAllDisliked] = useState<string[]>([])
  const categories = [...new Set(tracks.map((item: MusicTrack) => item.category))];

  const addTrack = (track: MusicTrack) => {
    dispatch({
      type: "ADD_TRACK",
      payload: track
    });
  };

  const removeTrack = (track: MusicTrack) => {
    dispatch({
      type: "REMOVE_TRACK",
      payload: track
    });
  };

  const updateTrack = (track: MusicTrack, updatedData: Partial<MusicTrack>) => {
    dispatch({
      type: "UPDATE_TRACK",
      payload: {
        id: track.id,
        updatedData
      }
    });
  };

  const moveTrack = (id: string, direction: Direction) => {
    dispatch({
      type: "MOVE_TRACK",
      payload: {
        id,
        direction
      }
    })
  };

  const changeFilter = (event: any) => {
    dispatch({
      type: "APPLY_FILTER",
      payload: {
        newFilter: event.target.value
      }
    })
  };

  const changeItemPerPage = (event: any) => {
    dispatch({
      type: "CHANGE_ITEM_PER_PAGE",
      payload: {
        newValue: event.target.value
      }
    })
  }

  const changePage = (newPage: number) => {
    if (page < 1 || page > tracks.length / itemPerPage) {
      return
    }
    dispatch({
      type: "CHANGE_PAGE",
      payload: {
        newPage
      }
    })
  }

  const likeTrack = (track: MusicTrack) => {
    if (allLiked.find(id => id === track.id)) {
      return
    }
    let disliked = false
    const idx = allDisliked.findIndex(dislikedId => dislikedId === track.id)
    if (idx !== -1) {
      allDisliked.splice(idx, 1)
      disliked = true
    }
    setAllLiked([...allLiked, track.id])
    dispatch({
      type: "UPDATE_TRACK",
      payload: {
        id: track.id,
        updatedData: { likes: track.likes + 1, dislikes: disliked ? track.dislikes - 1 : track.dislikes }
      }
    })
  }

  const dislikeTrack = (track: MusicTrack) => {
    if (allDisliked.find(id => id === track.id)) {
      return
    }
    let liked = false
    const idx = allLiked.findIndex(likedId => likedId === track.id)
    if (idx !== -1) {
      allLiked.splice(idx, 1)
      liked = true
    }
    setAllDisliked([...allDisliked, track.id])
    dispatch({
      type: "UPDATE_TRACK",
      payload: {
        id: track.id,
        updatedData: { dislikes: track.dislikes + 1, likes: liked ? track.likes - 1 : track.likes }
      }
    })
  }

  useEffect(() => {
    const currentIdx = (page - 1) * itemPerPage;
    if (filter !== "") {
      setFilteredTracks(tracks.filter((track: MusicTrack) => track.category === filter).slice(currentIdx, currentIdx + itemPerPage))
    } else {
      setFilteredTracks(tracks.slice(currentIdx, currentIdx + itemPerPage))
    }
  }, [filter, tracks, page, itemPerPage])

  useEffect(() => {
    dispatch(loadAsync())
  }, [dispatch])

  return (
    <Provider store={storeRedux.store}>
      <div className="App">
        <h1>Music Playlist</h1>
        <div>Status: {status}</div>
        <div>
          <h2>Tracks</h2>
            <select id="filter" onChange={(e) => changeFilter(e)}>
              <option value="">No filter</option>
              {categories.map((category: any, idx) => <option key={idx}>{category}</option>)}
            </select>
            <select id='itemPerPage' onChange={(e) => changeItemPerPage(e)}>
              <option value={4}>4</option>
              <option value={6}>6</option>
              <option value={8}>8</option>
            </select>
            <button onClick={() => changePage(page - 1)}>{'<'}</button>
            <button onClick={() => changePage(page + 1)}>{'>'}</button>
          {filteredTracks.length === 0 ? (
            <p>No tracks available</p>
          ) : (
            <ul>
              {filteredTracks.map((track: MusicTrack) => (
                <li key={track.id}>
                  <div>
                    <strong>{track.title}</strong> by {track.artist} | {track.category} - {track.likes} likes - {track.dislikes} dislikes
                    <button onClick={() => removeTrack(track)}>Remove</button>
                    <button
                      onClick={() =>
                        updateTrack(track, { title: "Updated Title" })
                      }
                    >
                      Update Title
                    </button>
                    <button onClick={() => moveTrack(track.id, Direction.UP)}>^</button>
                    <button onClick={() => moveTrack(track.id, Direction.DOWN)}>v</button>
                    <button onClick={() => likeTrack(track)}>like</button>
                    <button onClick={() => dislikeTrack(track)}>dislike</button>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </Provider>
  );
}

export default App;
