import { Provider, useDispatch, useSelector } from 'react-redux';
import './App.scss';
import { MusicTrack } from './assets/musics';
import storeRedux, { AppDispatch } from './redux/store';
import { useEffect } from 'react';
import { loadAsync } from './redux/rootReducer';

function App() {
  const dispatch = useDispatch<AppDispatch>()
  const { tracks, status } = useSelector((state: any) => {console.log(state); return state});

  const addTrack = (track: MusicTrack) => {
    dispatch({
      type: "ADD_TRACK",
      payload: track
    });
  };

  const removeTrack = (track: MusicTrack) => {
    dispatch({
      type: "REMOVE_TRACK",
      payload: track
    });
  };

  const updateTrack = (track: MusicTrack, updatedData: Partial<MusicTrack>) => {
    dispatch({
      type: "UPDATE_TRACK",
      payload: {
        id: track.id,
        updatedData
      }
    });
  };

  useEffect(() => {
    dispatch(loadAsync())
  })

  return (
    <Provider store={storeRedux.store}>
      <div className="App">
        <h1>Music Playlist</h1>
        <div>Status: {status}</div>
        <div>
          <h2>Tracks</h2>
          {tracks.length === 0 ? (
            <p>No tracks available</p>
          ) : (
            <ul>
              {tracks.map((track: MusicTrack) => (
                <li key={track.id}>
                  <div>
                    <strong>{track.title}</strong> by {track.artist}
                    <button onClick={() => removeTrack(track)}>Remove</button>
                    <button
                      onClick={() =>
                        updateTrack(track, { title: "Updated Title" })
                      }
                    >
                      Update Title
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </Provider>
  );
}

export default App;
