import { combineReducers } from "redux";

interface PlaylistState {
  tracks: Object[],
  status: 'idle' | 'loading' | 'failed'
}

const INITIAL_STATE: PlaylistState = {
  tracks: [],
  status: "idle"
}

export const Reducer = (
  state = {...INITIAL_STATE},
  action: any
) => {

  switch (action.type) {
    case "ADD_MOVIE":
      return { ...state, tracks: [...state.tracks, action.payload] };
    case "REMOVE_MOVIE":
      return { ...state, tracks: state.tracks.filter(movie => movie.id !== action.payload.id) };
    case "UPDATE_MOVIE":
      return {
        ...state,
        tracks: state.tracks.map(movie =>
          movie.id === action.payload.id ? { ...movie, ...action.payload.updatedData } : movie
        )
      };
    default:
      return state
  }
}

export const rootReducer = combineReducers({
  musics: Reducer
})


export default rootReducer;
